-- -----------------------------
-- 表结构 `muucmf_about`
-- -----------------------------
CREATE TABLE IF NOT EXISTS `muucmf_about` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL,
  `title` varchar(60) NOT NULL,
  `cover` int(11) NOT NULL COMMENT '顶图',
  `content` text NOT NULL,
  `status` tinyint(2) NOT NULL,
  `sort` int(6) NOT NULL,
  `update_time` int(11) NOT NULL,
  `create_time` int(11) NOT NULL,
  `category` int(11) NOT NULL COMMENT '分类id',
  `template` varchar(255) NOT NULL COMMENT '模板路径',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COMMENT='文章文章表';


-- -----------------------------
-- 表结构 `muucmf_about_category`
-- -----------------------------
CREATE TABLE IF NOT EXISTS `muucmf_about_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(25) NOT NULL,
  `sort` int(6) NOT NULL,
  `status` tinyint(2) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='文章分类';

-- -----------------------------
-- 表内记录 `muucmf_about`
-- -----------------------------
INSERT INTO `muucmf_about` VALUES ('3', '1', '原来你是这样的“火木”', '166', '<p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;火木科技是一家互联网创新型企业，火木科技希望能为传统企业和创业公司提供更快，更好，更专业的互联网技术解决方案提供助力。<br style=\"font-family: &#39;Helvetica Neue&#39;, Arial, sans; font-size: 16px; text-align: left;\"/>&nbsp;<br style=\"font-family: &#39;Helvetica Neue&#39;, Arial, sans; font-size: 16px; text-align: left;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;火木科技强调 创新、整合和开放，技术实力并非火木科技的至高追求，而是通过对互联网的理解为合作伙伴提供更有效解决切实问题的方法为价值所在。</p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;火木来源于古代中国哲学五行理论&quot;木、火、土、金、水&quot;五大元素之火与木构成。之所以选择“火”与“木”，是期望能帮助个别领域达到一种动态平衡。后人根据对五行的认识，又创造了五行相生相克理论，而火与木又正是彼此相生之两大元素。而根据五行学说之季节关系，春天属木，代表气体向四周扩散的运动方式。春天，花草树木生长茂盛，树木的枝条向四周伸展，养料往枝头输送。夏天属火，代表气体向上的运动方式。火的特点就是向上，夏天各种植物向上生长，长势迅猛。</p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;火木科技将专注基于互联网的技术服务平台及整合解决方案的打造，目前的产品体系也正是基于此逻辑开发。</p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其“木”系列产品专注为开发者提供服务，MuuCmf内容管理框架为开发者提供的一套标准化的技术方案。开发思想是将功能模块完全分离，随着模块数量和功能的不断增加和完善，在高速发展的互联网时代，MuuCmf将满足企业各项业务需求。“木”系列产品更强调的是横向发展和输出。</p><p><br style=\"text-align: left;\"/></p><p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其“火”系列产品是为初创企业和非技术性企业提供更优质的整合解决方案及技术合伙人服务。“火”系列产品更强调的是纵向发展和专注。</p><p style=\"text-align: center;\"><br/></p>', '1', '0', '1497145103', '1468587608', '1', '');
